var link = document.querySelector(".btn-feedback"), popup = document.querySelector(".write-us"), close = document.querySelector(".write-us-close"), fullname = document.querySelector("[name=fullname]"), openmap = document.querySelector(".open-map"), map = document.querySelector(".modal-map"), closemap = document.querySelector(".modal-map-close"), buy = document.querySelectorAll(".buy"), cart = document.querySelector(".cart"), closecart = document.querySelector(".cart-close");

if (link && link.addEventListener("click", function(e) {
    e.preventDefault(), popup.classList.add("write-us-show"), fullname.focus();
}), close && close.addEventListener("click", function(e) {
    e.preventDefault(), popup.classList.remove("write-us-show");
}), buy) for (var i = 0; i < buy.length; i++) buy[i].addEventListener("click", function(e) {
    e.preventDefault(), cart.classList.add("cart-show");
});

closecart && closecart.addEventListener("click", function(e) {
    e.preventDefault(), cart.classList.remove("cart-show");
}), openmap && openmap.addEventListener("click", function(e) {
    e.preventDefault(), map.classList.add("modal-map-show");
}), closemap && closemap.addEventListener("click", function(e) {
    e.preventDefault(), map.classList.remove("modal-map-show");
}), window.addEventListener("keydown", function(e) {
    27 === e.keyCode && map.classList.contains("modal-map-show") && map.classList.remove("modal-map-show");
});